async function attachFile() {
    return new Promise((resolve, reject) => {
        const endpoint = `/api/v1/alert/${ALERT_ID}/attachments`;
        const url = new URL(endpoint, THEHIVE_URL);

        console.log(`üåê URL: ${url.href}`);

        // Get binary data from n8n input
        const binaryData = $input.item.binary.data.data;
        const file_buffer = Buffer.from(binaryData, 'base64');

        console.log(`üì§ Preparing ${FILE_NAME} (${file_buffer.length} bytes) for TheHive alert ${ALERT_ID}`);

        // Build multipart body
        const boundary = "----WebKitFormBoundarybwyeFEHh4C1CJwfR";

        const body_parts = [
            `--${boundary}`,
            `Content-Disposition: form-data; name="attachments"; filename="${FILE_NAME}"`,
            `Content-Type: application/octet-stream`,
            `` // Empty line before binary content
        ];

        const header_text = Buffer.from(body_parts.join('\r\n') + '\r\n', 'utf-8');
        console.log(`üìã Header: ${header_text.length} bytes`);

        const footer_parts = [
            `\r\n--${boundary}\r\n`,
            `Content-Disposition: form-data; name="canRename"\r\n\r\ntrue\r\n--${boundary}--\r\n`
        ];
        const footer_text = Buffer.from(footer_parts.join(''), 'utf-8');
        console.log(`üìã Footer: ${footer_text.length} bytes`);

        // Combine all parts
        const multipart_body = Buffer.concat([header_text, file_buffer, footer_text]);
        console.log(`üì¶ Total body size: ${multipart_body.length} bytes`);

        // Headers
        const headers = {
            'Authorization': `Bearer ${API_KEY}`,
            'Accept': 'application/json, text/plain, */*',
            'Accept-Language': 'fr-FR,fr;q=0.9',
            'Content-Type': `multipart/form-data; boundary=${boundary}`,
            'Content-Length': multipart_body.length,
            'Origin': 'https://thehive.sbihi.soar.ma',
            'Referer': `https://thehive.sbihi.soar.ma/alerts/${ALERT_ID}/attachments`,
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
        };

        // Request options
        const requestModule = url.protocol === 'https:' ? https : http;
        const options = {
            hostname: url.hostname,
            port: url.port || (url.protocol === 'https:' ? 443 : 80),
            path: url.pathname,
            method: 'POST',
            headers: headers,
            // Add timeout and SSL options
            timeout: 30000,
            rejectUnauthorized: false // For self-signed certificates
        };

        console.log(`üîÑ Making POST request to ${url.pathname}`);

        // Make the request
        const req = requestModule.request(options, (res) => {
            let response_data = '';

            console.log(`üìä Response Status: ${res.statusCode}`);
            console.log(`üìã Response Headers:`, res.headers);

            res.on('data', (chunk) => {
                response_data += chunk;
            });

            res.on('end', () => {
                console.log(`üìã Response Body: ${response_data}`);

                const result = {
                    success: res.statusCode === 200 || res.statusCode === 201,
                    status_code: res.statusCode,
                    response: response_data,
                    file_info: {
                        name: FILE_NAME,
                        size: file_buffer.length,
                        uploaded_by: "imi-SB",
                        uploaded_at: "2025-08-03 21:06:26"
                    },
                    request_info: {
                        alert_id: ALERT_ID,
                        endpoint: endpoint,
                        body_size: multipart_body.length
                    }
                };

                if (result.success) {
                    console.log('‚úÖ File attached successfully!');
                } else {
                    console.log('‚ùå Attachment failed!');
                }

                resolve(result);
            });
        });

        req.on('error', (error) => {
            console.error('‚ùå Request error:', error);
            reject({
                success: false,
                error: error.message,
                file_info: {
                    name: FILE_NAME,
                    size: file_buffer.length
                }
            });
        });

        req.on('timeout', () => {
            console.error('‚ùå Request timeout');
            req.destroy();
            reject({
                success: false,
                error: 'Request timeout',
                file_info: {
                    name: FILE_NAME,
                    size: file_buffer.length
                }
            });
        });

        // Write the body and end the request
        req.write(multipart_body);
        req.end();
    });
}

// Execute the upload and return results
try {
    const result = await attachFile();

    return {
