function generateEternalBlueAlertEmail(alert) {
  function formatDate(ts) {
    if (!ts) return '';
    const date = new Date(ts);
    return date.toLocaleString('en-GB', { hour12: false });
  }
  function removeAsterisks(str) {
  return String(str || '').replace(/\*/g, '');
}
  function escapeHtml(str) {
    return String(str || '')
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;");
  }

  const body = alert.body || {};
  const tags = (alert.tags || []).map(tag => `<span class="alert-tag">${escapeHtml(tag)}</span>`).join(' ');
  const pcapStatus = body.pcap_file?.extraction_error
    ? `<span class="alert-error">‚ùå ${escapeHtml(body.pcap_file.extraction_error)}</span>`
    : (body.pcap_file?.filename
        ? `<span class="alert-success">‚úÖ PCAP extracted: ${escapeHtml(body.pcap_file.filename)}</span>`
        : `<span class="alert-warning">‚ö†Ô∏è No PCAP available</span>`
      );

  return `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>EternalBlue Security Alert</title>
  <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
  <style>
