      color: #222;
      margin: 0;
      padding: 0 0 40px 0;
    }
    .container {
      max-width: 650px;
      margin: 40px auto;
      background: #fff;
      border-radius: 18px;
      box-shadow: 0 8px 30px rgba(34,34,34,0.12);
      overflow: hidden;
      border: 1px solid #ececec;
    }
    .header {
      background: linear-gradient(135deg, #ff6b6b 0%, #ee5a24 100%);
      color: #fff;
      padding: 30px 30px 18px 30px;
      text-align: center;
    }
    .header h1 {
      margin: 0 0 10px 0;
      font-weight: 700;
      font-size: 2em;
      letter-spacing: 1px;
      text-shadow: 2px 2px 8px rgba(0,0,0,0.18);
    }
    .subtitle {
      font-size: 1.2em;
      opacity: 0.93;
      margin-bottom: 7px;
    }
    .alert-tags {
      margin-top: 8px;
    }
    .alert-tag {
      display: inline-block;
      background: #667eea;
      color: #fff;
      padding: 3px 11px;
      border-radius: 16px;
      margin: 2px 6px 2px 0;
      font-size: 0.90em;
      font-weight: 500;
      letter-spacing: 0.5px;
    }
    .section {
      padding: 35px 30px 20px 30px;
      border-bottom: 1px solid #f0f0f0;
    }
    .section:last-child {
      border-bottom: none;
    }
    .info-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(230px,1fr));
      gap: 20px;
      margin-bottom: 28px;
    }
    .info-card {
      background: #f8fafd;
      border-radius: 8px;
      box-shadow: 0 2px 6px rgba(102,126,234,0.07);
      padding: 18px 15px 14px 15px;
      border: 1px solid #ececec;
      margin-bottom: 10px;
    }
    .info-card h3 {
