      .section { padding: 18px 8px 13px 8px;}
    }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>EternalBlue Security Alert</h1>
      <div class="subtitle">Automated Report from Suricata &amp; SOAR</div>
      <div class="alert-tags">${tags}</div>
    </div>
    <div class="status-banner">
      ${escapeHtml(alert.title)}
    </div>
    <div class="section">
      <div class="info-grid">
        <div class="info-card">
          <h3>Alert Details</h3>
          <div class="info-item"><span class="info-label">Alert ID:</span> ${escapeHtml(alert._id)}</div>
          <div class="info-item"><span class="info-label">Type:</span> ${escapeHtml(alert.type)}</div>
          <div class="info-item"><span class="info-label">Status:</span> ${escapeHtml(alert.status)}</div>
          <div class="info-item"><span class="info-label">Severity:</span> <span class="alert-error">Level ${escapeHtml(alert.severity)}</span></div>
          <div class="info-item"><span class="info-label">Created:</span> ${formatDate(alert.createdAt)}</div>
        </div>
        <div class="info-card">
          <h3>Attack Info</h3>
          <div class="info-item"><span class="info-label">Source IP:</span> <span class="alert-error">${escapeHtml(body.source_ip)}</span></div>
          <div class="info-item"><span class="info-label">Target IP:</span> ${escapeHtml(body.destination_ip)}</div>
          <div class="info-item"><span class="info-label">Phase:</span> <span class="alert-error">${escapeHtml(body.attack_analysis?.phase)}</span></div>
          <div class="info-item"><span class="info-label">Attack Status:</span> <span class="alert-error">${escapeHtml(body.attack_analysis?.attack_status)}</span></div>
          <div class="info-item"><span class="info-label">Priority:</span> <span class="alert-error">${escapeHtml(body.attack_analysis?.priority_level)}</span></div>
          <div class="info-item"><span class="info-label">Detection:</span> ${formatDate(body.timestamp)}</div>
        </div>
      </div>
      <div class="critical-bloc">
        <div>Signature: ${escapeHtml(body.signature)}</div>
        <div>${pcapStatus}</div>
        ${body.error ? `<div class="alert-error">Error: ${escapeHtml(body.error)}</div>` : ''}
      </div>
      <div class="description">
        ${removeAsterisks(alert.description)}
      </div>
    </div>
    <div class="section">
      <div class="info-grid">
        <div class="info-card">
          <h3>Correlation</h3>
          <div class="info-item"><span class="info-label">Session ID:</span> ${escapeHtml(body.correlation_data?.session_id)}</div>
          <div class="info-item"><span class="info-label">Correlation File:</span> ${escapeHtml(body.correlation_data?.correlation_file)}</div>
        </div>
        <div class="info-card">
          <h3>Engine & Source</h3>
          <div class="info-item"><span class="info-label">Engine:</span> ${escapeHtml(body.detection_engine)}</div>
          <div class="info-item"><span class="info-label">Source:</span> ${escapeHtml(alert.source)}</div>
          <div class="info-item"><span class="info-label">Reference:</span> ${escapeHtml(alert.sourceRef)}</div>
        </div>
      </div>
      <div style="margin-top: 25px; text-align: center;">
